INSERT INTO `competence` (`code`, `name_competence`) VALUES ('УК-1', 'Знать закономерности исторического развития и формирования государственных и общественных институтов белорусского этноса, особенности развития науки, техники и технологий в разные исторические периоды, быть способным анализировать процессы государственного строительства и определять социально-политическое значение исторических событий, личностей, артефактов и символов');
INSERT INTO `competence` (`code`, `name_competence`) VALUES ('УК-2', 'Владеть высоким уровнем культуры политического мышления и поведения, понимать сущность, ценности и принципы идеологии белорусского государства, анализировать социально-политические процессы в стране и мире');
INSERT INTO `competence` (`code`, `name_competence`) VALUES ('УК-3', 'Владеть культурой мышления, быть способным к восприятию, обобщению и анализу информации, философских, мировоззренческих и психолого-педагогических проблем в сфере межличностных отношений и профессиональной деятельности ');
INSERT INTO `competence` (`code`, `name_competence`) VALUES ('УК-4', 'Уметь анализировать и оценивать социально-значимые явления, события и процессы, использовать социологическую и экономическую информацию при решении аналитических, научных и профессиональных задач, быть способным к проявлению предпринимательской инициативы ');
INSERT INTO `competence` (`code`, `name_competence`) VALUES ('УК-5', 'Обладать базовыми навыками коммуникации в устной и письменной формах на государственном и иностранном языках для решения задач межличностного и межкультурного взаимодействия и производственных задач');
INSERT INTO `competence` (`code`, `name_competence`) VALUES ('УК-6', 'Владеть навыками здоровьесбережения');
INSERT INTO `competence` (`code`, `name_competence`) VALUES ('УК-7', 'Владеть основами психологии труда для решения задач профессиональной деятельности');
INSERT INTO `competence` (`code`, `name_competence`) VALUES ('УК-8', 'Быть способным использовать основы правовых знаний в различных сферах жизнедеятельности, владеть навыками поиска нормативных правовых актов, анализа их содержания и применения для решения профессиональных задач');
INSERT INTO `competence` (`code`, `name_competence`) VALUES ('БПК-1', 'Владеть основными понятиями и методами линейной алгебры, аналитической и векторной геометрии, математического анализа, дифференциального и интегрального исчислений, анализа функций одной и нескольких переменных,  применять полученные знания для решения прикладных, инженерных задач');
INSERT INTO `competence` (`code`, `name_competence`) VALUES ('БПК-2', 'Владеть основными понятиями и законами физики, принципами экспериментального и теоретического изучения физических явлений и процессов, быть способным создавать и анализировать на основе физических законов теоретические модели, владеть навыками практического использования принципов и приемов физических измерений');
INSERT INTO `competence` (`code`, `name_competence`) VALUES ('БПК-3', 'Владеть теоретическими положениями химии, техникой химических расчетов и методами химических экспериментальных исследований, быть способным прогнозировать свойства соединений на основании строения вещества, характера химического и межмолекулярного взаимодействия ');
INSERT INTO `competence` (`code`, `name_competence`) VALUES ('БПК-4', 'Владеть методами защиты производственного персонала и населения от негативных воздействий факторов антропогенного, техногенного, естественного происхождения, знаниями основ рационального природопользования и энергосбережения  ');
INSERT INTO `competence` (`code`, `name_competence`) VALUES ('БПК-5', 'Быть способным применять в профессиональной деятельности правовые, организационные и инженерные основы обеспечения безопасных и здоровых условий труда, производить оценку условий труда, выявлять опасные и вредные производственные факторы, принимать решения по нормализации условий труда');
INSERT INTO `competence` (`code`, `name_competence`) VALUES ('БПК-6', 'Владеть методами графического изображения предметов на плоскости и в пространстве, навыками использования современного программного обеспечения для работы с графической информацией');
INSERT INTO `competence` (`code`, `name_competence`) VALUES ('БПК-7', 'Владеть базовыми навыками расчетов и испытаний электрических схем электрооборудования');
INSERT INTO `competence` (`code`, `name_competence`) VALUES ('БПК-8', 'Владеть инженерными методиками расчета, проектирования электронных устройств различного назначения, а также применения основных типов современных аналоговых и цифровых интегральных микросхем для решения инженерных задач');
INSERT INTO `competence` (`code`, `name_competence`) VALUES ('БПК-9', 'Быть способным производить расчеты и разрабатывать электронные средства автоматизации');
INSERT INTO `competence` (`code`, `name_competence`) VALUES ('БПК-10', 'Быть способным внедрять современные микропроцессорные системы автоматизации, осуществлять переналадку оборудования');
INSERT INTO `competence` (`code`, `name_competence`) VALUES ('БПК-11', 'Быть способным использовать основы экономических знаний в сфере лёгкой промышленности и других областях');
INSERT INTO `competence` (`code`, `name_competence`) VALUES ('БПК-12', 'Быть способным применять прогрессивные энергоэффективные и ресурсосберегающие технологии монтажа и наладки средств автоматизации');
INSERT INTO `competence` (`code`, `name_competence`) VALUES ('СК-1', 'Быть способным формировать стратегию управления производством, осуществлять  организационно-технические расчеты для планирования  основного и вспомогательного производств, выполнять оценку эффективности мероприятий по техническому и организационному развитию производства ');
INSERT INTO `competence` (`code`, `name_competence`) VALUES ('СК-2', 'Владеть основами организации, структуры и функционирования автоматизированных систем управления производством');
INSERT INTO `competence` (`code`, `name_competence`) VALUES ('СК-3', 'Быть способным применять физико-математические методы для расчетов механизмов, машин и конструкций, разрабатывать и анализировать их кинематические и динамические схемы');
INSERT INTO `competence` (`code`, `name_competence`) VALUES ('СК-4', 'Владеть основами состава, строения, свойств и обработки материалов');
INSERT INTO `competence` (`code`, `name_competence`) VALUES ('СК-5', 'Быть способным производить расчеты технических конструкций и их элементов на прочность, устойчивость, жесткость, знать устройство и принципы взаимодействия деталей машин общего назначения, определять рациональные варианты передач приводов машин и механизмов');
INSERT INTO `competence` (`code`, `name_competence`) VALUES ('СК-6', 'Быть способным выполнять расчет систем автоматизированного гидропневмопривода для использования в системах автоматизации');
INSERT INTO `competence` (`code`, `name_competence`) VALUES ('СК-7', 'Быть способным выполнять расчет систем автоматизированного электропривода для использования в системах автоматизации отрасли');
