INSERT INTO `competence` (`code`, `name_competence`) VALUES ('СК-15', 'Быть способным выполнять расчет технологического оборудования отрасли');
INSERT INTO `competence` (`code`, `name_competence`) VALUES ('СК-16', 'Быть способным применять современные системы автоматизированного проектирования систем управления ');
